mapper.select:
select * from t_store_amount
where del_flag = 0

	and store_id =#{storeId}


	and create_time >= #{startTime}


	and create_time <= #{endTime}


	and tenant_id = #{tenantId}


	and store_id in

		#{id}


order by create_time desc
limit #{offset}, #{size}

select COUNT(1) from t_store_amount
where del_flag = 0

	and store_id =#{storeId}


	and create_time >= #{startTime}


	and create_time <= #{endTime}


	and tenant_id = #{tenantId}


	and store_id in

		#{id}

mapper.update:
update t_store_amount


		balance = #{fresh.balance},


		update_time = #{fresh.updateTime},


where id = #{old.id}

	and balance= #{old.balance}

update t_store_amount
set del_flag = 1


	and store_id =#{id}
