mapper.select:
select
tsg.id, tsg.name, tsg.price,tsg.discount_price, tsg.del_flag,
tsg.create_time, tsg.update_time,tsg.tenant_id,tsg.store_id, tsg.car_model_id,tecm.name as carModel
from t_store_goods tsg left join t_electricity_car_model tecm on tsg.car_model_id=tecm.id
where tsg.del_flag=0

    and tsg.name = #{query.name}


    and tsg.create_time >=#{query.beginTime}


    and tsg.create_time <=#{query.endTime}


    and tsg.store_id = #{query.storeId}

order by tsg.create_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_store_goods
where del_flag=0

    and name = #{query.name}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and store_id = #{query.storeId}
