mapper.select:
select
id, uid, name, address, longitude, latitude, del_flag,
create_time, update_time,usable_status,service_phone, business_time
from t_store
where del_flag=0

    and name = #{query.name}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and address = #{query.address}


    and usable_status = #{query.usableStatus}


    and id in

        #{id}


order by create_time desc
limit #{query.offset}, #{query.size}

select id, uid, name, address, longitude, latitude, del_flag,
create_time, update_time,usable_status, service_phone, business_time, ROUND(
6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
#{query.lat} * PI() / 180 - latitude * PI() / 180
) / 2
),
2
) + COS(#{query.lat} * PI() / 180) * COS(latitude * PI() / 180) * POW(
SIN(
(
#{query.lon} * PI() / 180 - longitude * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS distance
from t_store
where del_flag = 0

    having distance < #{query.distance}

order by distance asc

select count(1) from t_store where del_flag=0
and id in
#{id}

select id, uid, name, address, longitude, latitude, del_flag, create_time, update_time,usable_status,service_phone, business_time from t_store where del_flag=0
and id in
#{id}
