mapper.select:
select
id, uid, name, address, longitude, latitude, del_flag,
create_time, update_time,usable_status,service_phone, business_time,franchisee_id,percent,pay_type
from t_store
where del_flag=0

    and name = #{query.name}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and address = #{query.address}


    and usable_status = #{query.usableStatus}


    and franchisee_id = #{query.franchiseeId}


    and uid = #{query.uid}


    and tenant_id = #{query.tenantId}


    and id in

        #{id}


order by create_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_store
where del_flag=0

    and name = #{query.name}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and address = #{query.address}


    and usable_status = #{query.usableStatus}


    and franchisee_id = #{query.franchiseeId}


    and uid = #{query.uid}


    and tenant_id = #{query.tenantId}


    and id in

        #{id}

select id, uid, name, address, longitude, latitude, del_flag,
create_time, update_time,usable_status, service_phone, business_time,pay_type, ROUND(
6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
#{query.lat} * PI() / 180 - latitude * PI() / 180
) / 2
),
2
) + COS(#{query.lat} * PI() / 180) * COS(latitude * PI() / 180) * POW(
SIN(
(
#{query.lon} * PI() / 180 - longitude * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS distance
from t_store
where del_flag = 0

    and tenant_id = #{query.tenantId}


    and name = #{query.name}


    having distance < #{query.distance}

order by distance asc

select count(1)
from t_store
where del_flag=0

    and id in

        #{id}



    and tenant_id = #{tenantId}

select from_unixtime(create_time / 1000, '%Y-%m-%d') date, count(*) as count
from t_store where create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}

    and id in

        #{id}



    and tenant_id = #{tenantId}

group by date
order by date desc

select ts.id from t_store ts left join t_city tc on ts.cid=tc.id left join t_province tp on tc.pid=tp.id
where ts.del_flag=0

    and tp.id = #{pid}


    and tc.id = #{cid}


    and ts.tenant_id = #{tenantId}

select count( case when #{tenantId} is null then case s.id when s.id then case s.del_flag when 0 then s.id end end else case tenant_id when #{tenantId} then case s.id when s.id then case s.del_flag when 0 then s.id end end end end ) as storeCount, c.pid as pid, c.id as cid, c.name from t_city c left join t_store s on s.cid=c.id
and c.pid = #{pid}
GROUP BY c.id
