mapper.select:
select
id, uid, name, address, longitude, latitude, del_flag,
create_time, update_time,usable_status,service_phone, business_time,franchisee_id,percent
from t_store
where del_flag=0

    and name = #{query.name}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and address = #{query.address}


    and usable_status = #{query.usableStatus}


    and franchisee_id = #{query.franchiseeId}


    and tenant_id = #{query.tenantId}


    and id in

        #{id}


order by create_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_store
where del_flag=0

    and name = #{query.name}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and address = #{query.address}


    and usable_status = #{query.usableStatus}


    and franchisee_id = #{query.franchiseeId}


    and tenant_id = #{query.tenantId}


    and id in

        #{id}


order by create_time desc

select id, uid, name, address, longitude, latitude, del_flag,
create_time, update_time,usable_status, service_phone, business_time, ROUND(
6378.138 * 2 * ASIN(
SQRT(
POW(
SIN(
(
#{query.lat} * PI() / 180 - latitude * PI() / 180
) / 2
),
2
) + COS(#{query.lat} * PI() / 180) * COS(latitude * PI() / 180) * POW(
SIN(
(
#{query.lon} * PI() / 180 - longitude * PI() / 180
) / 2
),
2
)
)
) * 1000
) AS distance
from t_store
where del_flag = 0

    and tenant_id = #{query.tenantId}


    having distance < #{query.distance}

order by distance asc

select count(1)
from t_store
where del_flag=0

    and id in

        #{id}



    and tenant_id = #{tenantId}

select from_unixtime(create_time / 1000, '%Y-%m-%d') date, count(*) as count
from t_store where create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}

    and id in

        #{id}



    and tenant_id = #{tenantId}

group by date
order by date desc
