mapper.select:
select
  id, tenant_id, api_call_stats, create_time, update_time
from t_tenant_calling_api_stats


        and id = #{id}


        and tenant_id = #{tenantId}


        and api_call_stats = #{apiCallStats}


        and create_time = #{createTime}


        and update_time = #{updateTime}

mapper.insert:
insert into t_tenant_calling_api_stats(tenant_id, api_call_stats, create_time, update_time)
values (#{tenantId}, #{apiCallStats}, #{createTime}, #{updateTime}) on duplicate key update
 api_call_stats = #{apiCallStats} + api_call_stats , update_time =#{updateTime}
