mapper.select:
select

from t_tenant_franchisee_mutual_exchange where tenant_id = #{tenantId}
and del_flag=0

select

from t_tenant_franchisee_mutual_exchange
where
del_flag = 0 and tenant_id = #{tenantId}

    and status = #{status}


    and combined_name like concat('%',#{combinedName},'%')

order by create_time desc
limit #{offset}, #{size}

select count(1)
from t_tenant_franchisee_mutual_exchange
where
del_flag = 0 and tenant_id = #{tenantId}

    and status = #{status}


    and combined_name like concat('%',#{combinedName},'%')
