mapper.select:
select
id, name, code, status, del_flag, create_time, update_time, expire_time
from t_tenant
where del_flag=0

	and name = #{name}


	and code = #{code}


	and status = #{status}


	and create_time >=#{startTime}


	and create_time <=#{endTime}

order by create_time desc
limit #{offset}, #{size}

select count(1)
where del_flag=0

	and name = #{name}


	and code = #{code}


	and status = #{status}


	and create_time >=#{startTime}


	and create_time <=#{endTime}

order by create_time desc
