mapper.select:
select tt.id, tt.name, tt.code, tt.status, tt.del_flag, tt.create_time, tt.update_time, tt.expire_time, tfrd.face_recognize_capacity, tecd.esign_capacity from t_tenant tt left join t_face_recognize_data tfrd on tt.id=tfrd.tenant_id left join t_esign_capacity_data tecd on tt.id=tecd.tenant_id where tt.del_flag=0
and tt.name like concat('%',#{name},'%')
and tt.code = #{code}
and tt.status = #{status}
and tt.create_time >=#{beginTime}
and tt.create_time <=#{endTime}
order by tt.expire_time limit #{offset}, #{size}

select count(1)
from t_tenant
where del_flag=0

	and name = #{name}


	and code = #{code}


	and status = #{status}


	and create_time >=#{beginTime}


	and create_time <=#{endTime}
