mapper.update:
update t_tenant_note
set note_num    = #{noteNum},
    update_time = #{updateTime}
where id = #{id}

mapper.select:
select id, tenant_id, note_num, recharge_time
from t_tenant_note
where tenant_id = #{tenantId};

mapper.update:
update t_tenant_note
set note_num = note_num - #{noteNum},
    update_time = #{updateTime}
where id = #{id}

update t_tenant_note
set note_num = note_num + #{noteNum},
    update_time = #{updateTime}
where id = #{id}
