mapper.select:
select
id, request_id, request_time, response_time, create_time, update_time, tenant_id, operate_type, attr_msg
from t_third_access_record


        and id = #{id}


        and request_id = #{requestId}


        and request_time = #{requestTime}


        and response_time = #{responseTime}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and tenant_id = #{tenantId}


        and operate_type = #{operateType}


        and attr_msg = #{attrMsg}

select
id, request_id, request_time, response_time, create_time, update_time, tenant_id, operate_type, attr_msg
from t_third_access_record

    tenant_id =#{tenantId}

        and request_id = #{requestId}


order by create_time desc
limit #{offset},#{size}
