mapper.select:
select
id, tenant_id, exchange_url, return_url, rent_url, create_time, update_time
from t_third_call_back_url


        and id = #{id}


        and tenant_id = #{tenantId}


        and exchange_url = #{exchangeUrl}


        and return_url = #{returnUrl}


        and rent_url = #{rentUrl}


        and create_time = #{createTime}


        and update_time = #{updateTime}
