mapper.select:
select
id, active_time, uid, phone, tenant_id, create_time, update_time,user_name
from t_user_active_info


        and id = #{id}


        and active_time = #{activeTime}


        and uid = #{uid}


        and phone = #{phone}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        user_name = #{userName},

mapper.insert:
insert into t_user_active_info(active_time, uid, phone, tenant_id, create_time, update_time, user_name)
values (#{activeTime}, #{uid}, #{phone}, #{tenantId}, #{createTime}, #{updateTime}, #{userName}) on duplicate key
update active_time=#{activeTime}, uid=#{uid},phone=#{phone}, update_time=#{updateTime}, user_name = #{userName}

mapper.select:
select
uai.id, uai.uid, uai.active_time, uai.phone, uai.user_name,
eb.sn as battery_sn, ubmc.card_pay_count as pay_count, ui.franchisee_id, ubmc.member_card_expire_time as memberCardExpireTime
from t_user_active_info uai
left join t_user_info ui force index (uid) on uai.uid = ui.uid
left join t_electricity_battery eb on uai.uid = eb.uid
left join t_user_battery_member_card ubmc on uai.uid = ubmc.uid

    ui.del_flag = 0

        and uai.phone = #{phone}


        and uai.user_name = #{userName}


        and uai.uid = #{uid}


        and eb.sn = #{batterySn}



            and eb.uid is null


            and eb.uid is not null



        and ubmc.card_pay_count = #{payCount}


        and uai.active_time <= #{limitTime}


        and uai.tenant_id = #{tenantId}


        and ui.franchisee_id in

            #{id}



        and ui.store_id in

            #{id}



order by uai.active_time
limit #{offset}, #{size}

select
count(*)
from t_user_active_info uai

    left join t_user_info ui force index (uid) on uai.uid = ui.uid


    left join t_electricity_battery eb on uai.uid = eb.uid


    left join t_user_battery_member_card ubmc on uai.uid = ubmc.uid


    ui.del_flag = 0

        and uai.phone = #{phone}


        and uai.user_name = #{userName}


        and uai.uid = #{uid}


        and eb.sn = #{batterySn}



            and eb.uid is null


            and eb.uid is not null



        and ubmc.card_pay_count = #{payCount}


        and uai.active_time <= #{limitTime}


        and uai.tenant_id = #{tenantId}


        and ui.franchisee_id in

            #{id}



        and ui.store_id in

            #{id}

mapper.update:
