mapper.select:
select
id, active_time, battery_id, battery_name, uid, phone, tenant_id, create_time, update_time,user_name
from t_user_active_info


        and id = #{id}


        and active_time = #{activeTime}


        and battery_id = #{batteryId}


        and battery_name = #{batteryName}


        and uid = #{uid}


        and phone = #{phone}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        user_name = #{userName},
