mapper.select:
select
id, active_time, uid, phone, tenant_id, create_time, update_time,user_name
from t_user_active_info


        and id = #{id}


        and active_time = #{activeTime}


        and uid = #{uid}


        and phone = #{phone}


        and tenant_id = #{tenantId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        user_name = #{userName},

select
uai.id, uai.uid, uai.active_time, uai.phone, uai.user_name,
ub.now_battery_sn as battery_sn, ubmc.card_pay_count as pay_count
from t_user_active_info uai
left join t_user_battery ub on uai.uid = ub.uid
left join t_user_battery_member_card ubmc on ubmc.uid = ub.uid


        and uai.phone = #{phone}


        and uai.user_name = #{userName}


        and ub.now_battery_sn = #{batterySn}


        and ubmc.card_pay_count = #{payCount}


        and uai.active_time <= #{limitTime}


        and uai.tenant_id = #{tenantId}


limit #{offset}, #{size}

select
count(*)
from t_user_active_info uai
left join t_user_battery ub on uai.uid = ub.uid
left join t_user_battery_member_card ubmc on ubmc.uid = ub.uid


        and uai.phone = #{phone}


        and uai.user_name = #{userName}


        and ub.now_battery_sn = #{batterySn}


        and ubmc.card_pay_count = #{payCount}


        and uai.active_time <= #{limitTime}


        and uai.tenant_id = #{tenantId}
