mapper.select:
select  fui.id, temc.name, fui.member_card_expire_time, ui.uid, fui.tenant_id, uob.third_id
        from t_user_battery_member_card fui
        left join t_user_info ui on fui.uid = ui.uid
        left join t_user_oauth_bind uob on uob.uid = ui.uid
        left join t_electricity_member_card temc on fui.member_card_id=temc.id
        where fui.del_flag = 0
        and fui.member_card_expire_time > #{firstTime}
        and fui.member_card_expire_time <= #{lastTime}
        limit #{offset}, #{size}

select  fui.id, emc.name as card_name, fui.rent_car_member_card_expire_time, ui.uid, fui.tenant_id, uob.third_id
        from t_franchisee_user_info fui
        left join t_user_info ui on fui.user_info_id = ui.id
        left join t_user_oauth_bind uob on uob.uid = ui.uid
        left join t_electricity_member_card emc on fui.rent_car_card_id = emc.id
        where fui.del_flag = 0
        and fui.rent_car_member_card_expire_time > #{firstTime}
        and fui.rent_car_member_card_expire_time <= #{lastTime}
        limit #{offset}, #{size}

select ubmc.uid, ubmc.order_id, ubmc.member_card_id, ubmc.member_card_expire_time, ubmc.order_expire_time, ubmc.remaining_number, ubmc.member_card_status, ubmc.disable_member_card_time, ubmc.card_pay_count from t_user_battery_member_card ubmc left join t_user_info tui on tui.uid=ubmc.uid where tui.battery_deposit_status=1 and tui.del_flag=0 and ubmc.member_card_status=0
and tui.tenant_id in
#{id}
limit #{offset},#{size}
