mapper.select:
select
tuc.id as id, tuc.name as name, tuc.uid as uid, tuc.phone as phone, tuc.coupon_id as couponId,
tuc.deadline as deadline, tuc.order_id as orderId,tuc.create_time as createTime,tc.superposition as superposition,
tuc.update_time as updateTime, tuc.status as status, tuc.source as source, tuc.del_flag as delFlag,
tuc.discount_type as discountType,tc.amount AS amount,tc.discount AS discount,tui.name as userName
from t_user_coupon as tuc
left join t_user_info tui on tuc.uid=tui.uid
left join t_coupon as tc on tuc.coupon_id=tc.id


        and tuc.uid = #{query.uid}


        and tuc.phone = #{query.phone}


        and tui.name = #{query.userName}


        and tuc.order_id = #{query.orderId}


        and tuc.coupon_id = #{query.couponId}


        and tuc.status = #{query.status}


        and tuc.discount_type = #{query.discountType}


        and tuc.status in

            #{id}



        and tuc.discount_type in

            #{id}



        and tuc.tenant_id = #{query.tenantId}


ORDER BY tuc.update_time DESC

    limit #{query.offset}, #{query.size}

select count(1)
from t_user_coupon as tuc left join t_user_info tui on tuc.uid=tui.uid


        and tuc.uid = #{query.uid}


        and tuc.phone = #{query.phone}


        and tuc.order_id = #{query.orderId}


        and tuc.coupon_id = #{query.couponId}


        and tui.name = #{query.userName}


        and tuc.discount_type = #{query.discountType}


        and tuc.status = #{query.status}


        and tuc.status in

            #{id}



        and tuc.discount_type in

            #{id}



        and tuc.tenant_id = #{query.tenantId}

mapper.update:
update t_user_coupon set
status = #{status},
order_id=#{orderId},
update_time = #{updateTime}
where id = #{id} and tenant_id = #{tenantId}

update.foreach:
update t_user_coupon set
status = #{item.status},
order_id=#{item.orderId},
update_time = #{item.updateTime}
where id = #{item.id}
