mapper.select:
select
id, uid, name, phone, id_number, service_status, member_card_expire_time, remaining_number,card_type,battery_store_id,
battery_area_id,
init_electricity_battery_sn, now_electricity_battery_sn, battery_deposit, car_store_id, car_sn, number_plate,
car_deposit, usable_status, del_flag, create_time, update_time
from electricity.t_user_info
where del_flag=0

    and name = #{query.name}


    and phone = #{query.phone}


    and battery_area_id = #{query.batteryAreaId}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}

order by update_time desc
limit #{query.offset}, #{query.size}
