mapper.select:
select
id, uid, user_name,name, phone, id_number, service_status,auth_status,
usable_status, del_flag, create_time,
update_time,tenant_id
from t_user_info
where del_flag=0

    and name = #{query.name}


    and phone = #{query.phone}


    and tenant_id = #{query.tenantId}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and service_status = #{query.serviceStatus}


    and service_status = #{query.serviceStatus}


    and auth_status is not null

order by create_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_user_info
where del_flag=0

    and name = #{query.name}


    and phone = #{query.phone}


    and tenant_id = #{query.tenantId}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and service_status = #{query.serviceStatus}


    and service_status = #{query.serviceStatus}


    and auth_status is not null

select from_unixtime(create_time / 1000, '%Y-%m-%d') date, count(*) as count from t_user_info where del_flag=0 and create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}
and tenant_id = #{tenantId}
group by date order by date desc

select count(1)
from t_user_info
where del_flag=0
and create_time >=#{first}
and create_time <=#{now}

    and tenant_id = #{tenantId}
