mapper.select:
select
id, uid, user_name,real_name, phone, id_number, status,auth_status, member_card_expire_time,remaining_number,card_type,
init_electricity_battery_sn, now_electricity_battery_sn, battery_deposit, usable_status, del_flag, create_time, update_time,mailbox
from electricity.t_user_info
where del_flag=0

    and name = #{query.name}


    and phone = #{query.phone}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and status = #{query.status}

order by create_time desc

mapper.update:
update electricity.t_user_info set
    now_electricity_battery_sn = #{nowElectricityBatterySn},
    update_time = #{updateTime}
where uid = #{uid}
