mapper.select:
select
  id, uid, name, phone, store_id, id_number, init_electricity_battery_sn, now_electricity_battery_sn, service_status, member_card_days, deposit, del_flag, create_time, update_time
from electricity.t_user_info


        and id = #{id}


        and uid = #{uid}


        and name = #{name}


        and phone = #{phone}


        and store_id = #{storeId}


        and id_number = #{idNumber}


        and init_electricity_battery_sn = #{initElectricityBatterySn}


        and now_electricity_battery_sn = #{nowElectricityBatterySn}


        and service_status = #{serviceStatus}


        and member_card_days = #{memberCardDays}


        and deposit = #{deposit}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}
