mapper.select:
select
  id, uid, name, phone, id_number, service_status, member_card_days, battery_store_id, battery_area_id, init_electricity_battery_sn, now_electricity_battery_sn, battery_deposit, car_store_id, car_sn, number_plate, car_deposit, usable_status, del_flag, create_time, update_time
from electricity.t_user_info


        and id = #{id}


        and uid = #{uid}


        and name = #{name}


        and phone = #{phone}


        and id_number = #{idNumber}


        and service_status = #{serviceStatus}


        and member_card_days = #{memberCardDays}


        and battery_store_id = #{batteryStoreId}


        and battery_area_id = #{batteryAreaId}


        and init_electricity_battery_sn = #{initElectricityBatterySn}


        and now_electricity_battery_sn = #{nowElectricityBatterySn}


        and battery_deposit = #{batteryDeposit}


        and car_store_id = #{carStoreId}


        and car_sn = #{carSn}


        and number_plate = #{numberPlate}


        and car_deposit = #{carDeposit}


        and usable_status = #{usableStatus}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}
