mapper.select:
select
id, uid, user_name,name, phone, id_number,mailbox, service_status,auth_status,
member_card_expire_time,remaining_number,card_type,
init_electricity_battery_sn, now_electricity_battery_sn, battery_deposit, usable_status, del_flag, create_time,
update_time
from electricity.t_user_info
where del_flag=0

    and name = #{query.name}


    and phone = #{query.phone}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and service_status = #{query.serviceStatus}


    and auth_status is not null

order by create_time desc

select count(1)
from electricity.t_user_info where create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}
and member_card_expire_time is not null and remaining_number is not null

mapper.update:
update electricity.t_user_info set

    service_status = #{serviceStatus},

now_electricity_battery_sn = #{nowElectricityBatterySn},
update_time = #{updateTime}
where uid = #{uid}

mapper.select:
select
id, uid, user_name,name, phone, id_number,mailbox, service_status,auth_status,
member_card_expire_time,remaining_number,card_type,
init_electricity_battery_sn, now_electricity_battery_sn, battery_deposit, usable_status, del_flag, create_time,
update_time
from electricity.t_user_info
where del_flag=0

    and name = #{query.name}


    and phone = #{query.phone}


    and create_time >=#{query.beginTime}


    and create_time <=#{query.endTime}


    and service_status = #{query.serviceStatus}


    and auth_status is not null

order by create_time desc

select count(1)
from electricity.t_user_info as tui
left join electricity.t_user as tu
where tui.del_flag=0
and tui.create_time >=#{first}
and tui.create_time <=#{now}

    and tu.cid in

        #{id}

select count(1)
from electricity.t_user_info as tui
left join electricity.t_user as tu
where tui.del_flag=0 and service_status=1
and tui.create_time >=#{first}
and tui.create_time <=#{now}

    and tu.cid in

        #{id}
