mapper.select:
SELECT FROM
`del_flag` = 0
ORDER BY `create_time` DESC LIMIT #{offset}, #{size}

select
tui.id as id, tui.uid as uid, tui.name as name,tui.auth_type,
tui.phone as phone, tui.id_number as idNumber,tui.battery_rent_status,tui.battery_deposit_status,
tui.auth_status as authStatus,tui.usable_status as usableStatus, tui.del_flag as delFlag,
tui.create_time as createTime,tui.update_time as updateTime,tui.tenant_id as tenantId
from t_user_info as tui
left join t_user_battery_member_card as tfui on tui.uid=tfui.uid
where tui.del_flag=0

    and tui.name = #{query.name}


    and tui.phone = #{query.phone}


    and tui.uid = #{query.uid}


    and tui.tenant_id = #{query.tenantId}


    and tui.create_time >=#{query.beginTime}


    and tui.create_time <=#{query.endTime}


    and tui.auth_status =#{query.authStatus}


    and tui.auth_type =#{query.authType}


    and tui.auth_status in (0,1,2)


    and tui.franchisee_id in

        #{id}



    and tui.store_id in

        #{id}


order by tui.create_time desc
limit #{query.offset}, #{query.size}

select
tui.id as user_info_id,
tui.phone,
tui.name,
tui.uid,
tui.create_time as userCertificationTime,
tui.auth_status,
tui.usable_status,
tui.battery_rent_status,
tui.franchisee_id,
tui.battery_deposit_status,
tui.pay_count,
ubmc.member_card_expire_time,
ubmc.disable_member_card_time,
ubmc.remaining_number,
ubmc.member_card_id as member_card_id,
ubmc.card_pay_count as cardPayCount,
tui.tenant_id
from t_user_info tui
left join t_user_battery_member_card ubmc on ubmc.uid=tui.uid
left join t_electricity_battery teb on tui.uid=teb.uid
where tui.del_flag=0 and tui.auth_status=2

    and tui.tenant_id = #{query.tenantId}


    and tui.name = #{query.name}


    and tui.phone = #{query.phone}


    and teb.id = #{query.batteryId}


    and tui.uid = #{query.uid}


    and ubmc.member_card_id = #{query.memberCardId}


    and tui.create_time >=#{query.beginTime}


    and tui.create_time <=#{query.endTime}


    and tui.auth_status =#{query.authStatus}


    and tui.battery_rent_status =#{query.batteryRentStatus}


    and tui.battery_deposit_status =#{query.batteryDepositStatus}


    and ubmc.member_card_expire_time >=#{query.memberCardExpireTimeBegin}


    and ubmc.member_card_expire_time <=#{query.memberCardExpireTimeEnd}


    and ubmc.member_card_expire_time is not null and ubmc.member_card_expire_time !=0


    and ubmc.card_pay_count =#{query.cardPayCount}


    and tui.franchisee_id in

        #{id}



    and tui.store_id in

        #{id}



    and tui.create_time >=#{query.userCreateBeginTime}


    and tui.create_time <=#{query.userCreateEndTime}


    order by ubmc.member_card_expire_time desc


    order by tui.create_time desc

limit #{query.offset}, #{query.size}

