select ui.uid,ui.user_name AS userName,ui.phone as phone, ubmc.card_pay_count AS payCount, ubmc.member_card_expire_time AS expireTime,uai.active_time AS activeTime,eb.sn AS batterySn from t_user_info ui left join t_user_active_info uai on ui.uid = uai.uid left join t_electricity_battery eb on eb.uid = eb.uid left join t_user_battery_member_card ubmc on ubmc.uid = ui.uid where ui.del_flag = 0 and ui.tenant_id = #{query.tenantId} and UNIX_TIMESTAMP() > ubmc.member_card_expire_time
and eb.sn = #{query.batterySn}
and ui.phone = #{query.phone}
and ui.user_name like concat('%',#{userName, jdbcType=VARCHAR},'%')
order by id limit #{query.offset}, #{query.size}

