select ui.uid,ui.user_name AS userName,ui.phone as phone, ubmc.sn AS batterySn, ubmc.card_pay_count AS payCount, ubmc.member_card_expire_time AS expireTime,uai.active_time AS activeTime from t_user_info ui left join t_user_active_info uai on ui.uid = uai.uid left join t_electricity_battery eb on eb.uid = eb.uid left join t_user_battery_member_card ubmc on ubmc.uid = ui.uid where ui.del_flag = 0 and ui.tenant_id = #{query.tenantId} and uai.active_time > UNIX_TIMESTAMP(DATE_SUB(NOW(), INTERVAL 30 DAY))
and eb.sn = #{query.batterySn}
and ui.phone = #{query.phone}
and ui.user_name like concat('%',#{userName, jdbcType=VARCHAR},'%')

select count(*) from t_user_info ui left join t_user_active_info uai on ui.uid = uai.uid left join t_electricity_battery eb on eb.uid = eb.uid left join t_user_battery_member_card ubmc on ubmc.uid = ui.uid where ui.del_flag = 0 and ui.tenant_id = #{query.tenantId} and uai.active_time > UNIX_TIMESTAMP(DATE_SUB(NOW(), INTERVAL 30 DAY))
and eb.sn = #{query.batterySn}
and ui.phone = #{query.phone}
and ui.user_name like concat('%',#{userName, jdbcType=VARCHAR},'%')

SELECT tui.NAME, tui.phone, ubmc.card_pay_count AS cardPayCount, tui.id AS user_info_id, teb.sn, tui.battery_rent_status, tui.battery_deposit_status, ubmc.member_card_id, ubmc.member_card_status, ubmc.member_card_expire_time, tiui.is_use, tiui.insurance_expire_time, tui.uid, tui.create_time AS userCertificationTime, tui.franchisee_id, tui.tenant_id, ubmc.remaining_number AS limitCount, tui.usable_status FROM t_user_info tui LEFT JOIN t_user_battery_member_card ubmc ON ubmc.uid = tui.uid AND ubmc.del_flag = 0 LEFT JOIN t_electricity_battery teb ON tui.uid = teb.uid AND teb.del_flag = 0 LEFT JOIN t_insurance_user_info tiui ON tui.uid = tiui.uid AND tiui.del_flag = 0 WHERE tui.del_flag = 0 AND tui.auth_status = 2 AND tui.tenant_id =#{query.tenantId}
and ubmc.member_card_expire_time >=#{query.memberCardExpireTimeBegin}
and ubmc.member_card_expire_time <=#{query.memberCardExpireTimeEnd}
and tui.battery_rent_status =#{query.batteryRentStatus}
and ubmc.member_card_expire_time is not null and ubmc.member_card_expire_time !=0
and tui.uid = #{query.uid}
and tui.franchisee_id in
#{id}
and tui.store_id in
#{id}
order by ISNULL(ubmc.member_card_expire_time),ubmc.member_card_expire_time limit #{query.offset}, #{query.size}

SELECT
    count(1)
FROM
t_user_info tui
LEFT JOIN t_user_battery_member_card ubmc ON ubmc.uid = tui.uid
AND ubmc.del_flag = 0
LEFT JOIN t_electricity_battery teb ON tui.uid = teb.uid
AND teb.del_flag = 0
LEFT JOIN t_insurance_user_info tiui ON tui.uid = tiui.uid
AND tiui.del_flag = 0
WHERE
tui.del_flag = 0
AND tui.auth_status = 2
AND tui.tenant_id =#{query.tenantId}

    and ubmc.member_card_expire_time >=#{query.memberCardExpireTimeBegin}


    and ubmc.member_card_expire_time <=#{query.memberCardExpireTimeEnd}


    and tui.battery_rent_status =#{query.batteryRentStatus}


    and ubmc.member_card_expire_time is not null and ubmc.member_card_expire_time !=0


    and tui.uid = #{query.uid}


    and tui.franchisee_id in

        #{id}



    and tui.store_id in

        #{id}
