select from_unixtime(create_time / 1000, '%Y-%m-%d') date, count(*) as count from t_user_info where del_flag=0 and create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}
and tenant_id = #{tenantId}
group by date order by date desc

select count(1)
from t_user_info
where del_flag=0
and create_time >=#{first}
and create_time <=#{now}

    and tenant_id = #{tenantId}

select count(1) as userCount,FROM_UNIXTIME( tfui.create_time / 1000, '%Y-%m-%d' ) as
weekDate
from t_user_info as tfui
where tfui.del_flag=0 and tfui.service_status=1

    and tfui.tenant_id = #{tenantId}


    and tfui.create_time >=#{beginTime}


    and tfui.create_time <=#{endTime}

GROUP BY
weekDate

select count(1) as userCount,FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) as
weekDate
from t_user
where del_flag=0

    and tenant_id = #{tenantId}


    and user_type =#{userType}


    and create_time >=#{beginTime}


    and create_time <=#{endTime}

GROUP BY
weekDate
