mapper.select:
select
tui.id as id, tui.uid as uid, tui.user_name as userName,tui.name as name,
tui.phone as phone, tui.id_number as idNumber, tui.service_status as serviceStatus,
tui.auth_status as authStatus,tui.usable_status as usableStatus, tui.del_flag as delFlag,
tui.create_time as createTime,tui.update_time as updateTime,tui.tenant_id as tenantId
from t_user_info as tui
left join t_franchisee_user_info as tfui on tui.id=tfui.user_info_id
where tui.del_flag=0

    and tui.name = #{query.name}


    and tui.phone = #{query.phone}


    and tui.tenant_id = #{query.tenantId}


    and tui.create_time >=#{query.beginTime}


    and tui.create_time <=#{query.endTime}


    and tui.auth_status =#{query.authStatus}


    and tui.auth_status in (0,1,2)


    and tui.service_status =#{query.serviceStatus}


    and tfui.service_status =#{query.serviceStatus}


    and tfui.service_status =#{query.serviceStatus}

order by tfui.create_time desc
limit #{query.offset}, #{query.size}

select
tui.phone,tui.name,tui.uid,tfui.now_electricity_battery_sn,tfui.service_status,tfui.card_name,tfui.member_card_expire_time,
tfui.battery_deposit,tfui.franchisee_id,tfui.card_id,teb.power,teb.model from t_user_info tui
left join t_franchisee_user_info tfui on tfui.user_info_id=tui.id
left join t_electricity_battery teb on tfui.now_electricity_battery_sn=teb.sn
where tui.del_flag=0

    and tui.tenant_id = #{query.tenantId}


    and tui.name = #{query.name}


    and tui.phone = #{query.phone}


    and tui.uid = #{query.uid}


    and tfui.card_id = #{query.memberCardId}


    and tui.create_time >=#{query.beginTime}


    and tui.create_time <=#{query.endTime}


    and tui.auth_status =#{query.authStatus}


    and tfui.member_card_expire_time >=#{query.memberCardExpireTimeBegin}


    and tfui.member_card_expire_time <=#{query.memberCardExpireTimeEnd}


    and tui.service_status =#{query.serviceStatus}


    and tfui.service_status =#{query.serviceStatus}


    and tfui.service_status =#{query.serviceStatus}

order by tfui.member_card_expire_time desc
limit #{query.offset}, #{query.size}

select COUNT(1)
from t_user_info as tui
left join t_franchisee_user_info as tfui on tui.id=tfui.user_info_id
where tui.del_flag=0

    and tui.name = #{query.name}


    and tui.phone = #{query.phone}


    and tui.uid = #{query.uid}


    and tui.tenant_id = #{query.tenantId}


    and tfui.card_id = #{query.memberCardId}


    and tui.create_time >=#{query.beginTime}


    and tui.create_time <=#{query.endTime}


    and tui.auth_status =#{query.authStatus}


    and tui.auth_status in (0,1,2)


    and tui.service_status =#{query.serviceStatus}


    and tfui.service_status =#{query.serviceStatus}


    and tfui.service_status =#{query.serviceStatus}

select from_unixtime(create_time / 1000, '%Y-%m-%d') date, count(*) as count from t_user_info where del_flag=0 and create_time >= #{startTimeMilliDay} and create_time <=#{endTimeMilliDay}
and tenant_id = #{tenantId}
group by date order by date desc

select count(1)
from t_user_info
where del_flag=0
and create_time >=#{first}
and create_time <=#{now}

    and tenant_id = #{tenantId}
