mapper.select:
select
uid, phone, login_pwd, salt, avatar, name, gender, del_flag, lock_flag, user_type, create_time, update_time,city,province,address,cid,
lang,ref_id
from electricity.t_user


		and uid = #{uid}


		and phone = #{phone}


		and login_pwd = #{loginPwd}


		and salt = #{salt}


		and avatar = #{avatar}


		and name = #{name}


		and gender = #{gender}


		and del_flag = #{delFlag}


		and lock_flag = #{lockFlag}


		and user_type = #{userType}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and lang = #{lang}

select uid, phone, avatar, name, gender, del_flag, lock_flag, user_type, create_time, update_time,
city,province,address,cid,ref_id
lang
from t_user

	del_flag = 0

		and uid = #{uid}


		and name like concat('%',#{name},'%')


		and user_type = #{type}


		and phone=#{phone}


		and create_time >=#{startTime}


		and create_time <=#{endTime}


order by create_time desc
