mapper.select:
select uid, phone, avatar, name, gender, del_flag, lock_flag, user_type, create_time, update_time,
city,province,address,cid,ref_id,lang,tenant_id,data_type
from t_user

	del_flag = 0

		and uid = #{uid}


		and name like concat('%',#{name},'%')


		and user_type = #{type}


		and user_type !=11


		and phone=#{phone}


		and create_time >=#{startTime}


		and create_time <=#{endTime}


		and tenant_id = #{tenantId}


order by create_time desc
limit #{offset}, #{size}

select COUNT(1)
from t_user

	del_flag = 0

		and uid = #{uid}


		and name like concat('%',#{name},'%')


		and user_type = #{type}


		and user_type !=11


		and phone=#{phone}


		and create_time >=#{startTime}


		and create_time <=#{endTime}


		and tenant_id = #{tenantId}

select
tu.uid,
tu.phone,
tu.source,
tu.ref_id as electricityCabinetId,
tui.create_time,
tui.update_time,
tui.name,
tui.franchisee_id

from t_user tu left join t_user_info tui on tu.uid=tui.uid

	tu.del_flag = 0

		and tu.uid = #{uid}


		and tui.name like concat('%',#{name},'%')


		and tu.phone=#{phone}


		and tui.franchisee_id=#{franchiseeId}


		and tu.ref_id=#{electricityCabinetId}


		and tui.tenant_id = #{tenantId}


		and tui.create_time >=#{startTime}


		and tui.create_time <=#{endTime}


		and tu.ref_id in

			#{id}



order by tui.create_time desc
limit #{offset}, #{size}

select COUNT(1)
from t_user tu  left join t_user_info tui on tu.uid=tui.uid

	tu.del_flag = 0

		and tu.uid = #{uid}


		and tui.name like concat('%',#{name},'%')


		and tu.phone=#{phone}


		and tui.franchisee_id=#{franchiseeId}


		and tu.ref_id=#{electricityCabinetId}


		and tui.tenant_id = #{tenantId}


		and tui.create_time >=#{startTime}


		and tui.create_time <=#{endTime}


		and tu.ref_id in

			#{id}
