mapper.select:
select
id, status, begin_time, end_time, title, content, create_time, update_time, tenant_id
from t_user_notify


        and id = #{id}


        and status = #{status}


        and begin_time = #{beginTime}


        and end_time = #{endTime}


        and title = #{title}


        and content = #{content}


        and create_time = #{createTime}


        and update_time = #{updateTime}

select
id, status, begin_time, end_time, title, content, create_time, update_time, tenant_id
from t_user_notify where tenant_id = #{tenantId}
