mapper.select:
select
id, uid, source, third_id, third_nick, access_token, refresh_token, status, create_time, update_time,phone
from electricity.t_user_oauth_bind


        and id = #{id}


        and uid = #{uid}


        and source = #{source}


        and third_id = #{thirdId}


        and third_nick = #{thirdNick}


        and access_token = #{accessToken}


        and refresh_token = #{refreshToken}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}

select
id, uid, source, third_id, third_nick, access_token, refresh_token, status, create_time, update_time,phone
from electricity.t_user_oauth_bind


        phone = #{phone}


        third_id = #{thirdId}


        uid = #{uid}


order by create_time desc
limit #{offset}, #{size}
