mapper.select:
select
id, uid, source, third_id, third_nick, access_token, refresh_token, status, create_time, update_time,phone
from t_user_oauth_bind


        and phone = #{phone}


        and third_id = #{thirdId}


        and uid = #{uid}


        and tenant_id = #{tenantId}


order by create_time desc
limit #{offset}, #{size}
