mapper.select:
select
id, tenant_id, open_ids, create_time, update_time, del_flag
from t_wechat_template_admin_notification


        and id = #{id}


        and tenant_id = #{tenantId}


        and open_ids = #{openIds}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and del_flag = #{delFlag}
