mapper.select:
select

from t_asset_allocate_record


        and order_no = #{orderNo}


        and type = #{type}


        and old_franchisee_id= #{sourceFranchiseeId}


        and new_franchisee_id = #{targetFranchiseeId}

    and tenant_id = #{tenantId} and del_flag = 0

order by id desc
limit #{offset}, #{size}

select count(1)
from t_asset_allocate_record


        and order_no = #{orderNo}


        and type = #{type}


        and old_franchisee_id= #{sourceFranchiseeId}


        and new_franchisee_id = #{targetFranchiseeId}

    and tenant_id = #{tenantId} and del_flag = 0
