mapper.insert:
insert into t_asset_exit_warehouse_record
order_no, franchisee_id, store_id, type, warehouse_id, operator, tenant_id, del_flag, create_time, update_time, remark
#{orderNo}, #{franchiseeId}, #{storeId}, #{type}, #{warehouseId}, #{operator}, #{tenantId}, #{delFlag}, #{createTime}, #{updateTime}, #{remark}

mapper.select:
select taewr.id, taewr.order_no, taewr.franchisee_id, taewr.store_id, taewr.type, taewr.warehouse_id, taewr.operator, taewr.tenant_id, taewr.del_flag, taewr.create_time,
taewr.update_time, taewr.remark, taw.name as warehouseName
from t_asset_exit_warehouse_record taewr
left join t_asset_warehouse taw on taw.id = taewr.warehouse_id

    tenant_id = #{tenantId} and del_flag = 0

        and order_no=#{orderNo}


        and franchisee_id=#{franchiseeId}


        and type=#{type}


order by id desc
limit #{offset}, #{size}

select count(1)
from t_asset_exit_warehouse_record

    tenant_id = #{tenantId} and del_flag = 0

        and order_no=#{orderNo}


        and franchisee_id=#{franchiseeId}


        and type=#{type}
