mapper.update:
update t_asset_inventory_detail
set inventoryStatus = 1, operator = #{uid}, status = #{status}
where sn in

    #{item}

and tenant_id = #{tenantId} and del_flag=0

mapper.select:
select

from t_asset_inventory_detail


        and order_no=#{orderNo}


        and status=#{status}


        and franchisee_id=#{franchiseeId}

    and tenant_id = #{tenantId} and del_flag = 0

ORDER BY id
limit #{offset}, #{size}

select count(1)
from t_asset_inventory_detail


        and order_no=#{orderNo}


        and status=#{status}


        and franchisee_id=#{franchiseeId}

    and tenant_id = #{tenantId} and del_flag = 0
