mapper.insert:
insert into t_asset_inventory
order_no, franchisee_id, type, status, inventoried_total, pending_total, finish_time, operator, tenant_id, del_flag, create_time, update_time, remark
#{orderNo}, #{franchiseeId}, #{type}, #{status}, #{inventoriedTotal}, #{pendingTotal}, #{finishTime}, #{operator}, #{tenantId}, #{delFlag}, #{createTime}, #{updateTime}, #{remark}

mapper.update:
update t_asset_inventory
set inventoried_total = inventoried_total + #{inventoryCount},
    pending_total = pending_total - #{inventoryCount},
    status = #{status},
    update_time = #{updateTime}
where tenant_id = #{tenantId}
  and order_no = #{orderNo};

mapper.select:
select

from t_asset_inventory


        and order_no=#{orderNo}


        and franchisee_id=#{franchiseeId}


        and status=#{status}


        and franchisee_id in

            #{id}


    and tenant_id = #{tenantId} and del_flag = 0

order by id desc
limit #{offset}, #{size}

select count(1)
from t_asset_inventory


        and order_no=#{orderNo}


        and franchisee_id=#{franchiseeId}


        and status=#{status}


        and franchisee_id in

            #{id}


    and tenant_id = #{tenantId} and del_flag = 0

select

from t_asset_inventory


        and order_no=#{orderNo}


        and tenant_id=#{tenantId}

    and del_flag = 0

select 1 from t_asset_inventory where tenant_id = #{tenantId} and franchisee_id in


        #{item}


  and type = #{type} and status = 0 order by id desc limit 1;
