mapper.insert:
insert into t_asset_warehouse
id, name, status, manager_name, manager_phone, address, del_flag, tenant_id, create_time, update_time
#{id}, #{name}, #{status}, #{managerName}, #{managerPhone}, #{address}, #{delFlag}, #{tenantId}, #{createTime}, #{updateTime}

mapper.select:
select id, name
from t_asset_warehouse
where tenant_id = #{tenantId}
  and del_flag = 0;
