mapper.insert:
insert into t_asset_warehouse
name, status, manager_name, manager_phone, address, del_flag, tenant_id, create_time, update_time, franchisee_id
#{name}, #{status}, #{managerName}, #{managerPhone}, #{address}, #{delFlag}, #{tenantId}, #{createTime}, #{updateTime}, #{franchiseeId}

mapper.select:
SELECT
FROM t_asset_warehouse
and name like concat('%', #{name}, '%')
and franchisee_id = #{franchiseeId}
and tenant_id = #{tenantId} and del_flag = 0
ORDER BY id DESC limit #{offset}, #{size}
