mapper.insert:
insert into t_asset_warehouse_record
record_no, type, operate_type, warehouse_id, operator, tenant_id, del_flag, create_time, update_time, remark
#{recordNo}, #{type}, #{operateType}, #{warehouseId}, #{operator}, #{tenantId}, #{delFlag}, #{createTime}, #{updateTime}, #{remark}

mapper.select:
SELECT tawr.id, tawr.record_no, tawr.type, tawr.operate_type, tawr.warehouse_id, tawr.operator, tawr.tenant_id, tawr.del_flag, tawr.create_time, tawr.update_time,
tawr.remark, taw.name as warehouseName
FROM t_asset_warehouse_record tawr
LEFT JOIN t_asset_warehouse taw ON tawr.warehouse_id = taw.id

    tawr.warehouse_id = #{warehouseId} and tawr.del_flag = 0

        and tawr.type = #{type}


        and record_no in

            #{recordNo}



ORDER BY tawr.id DESC
limit #{offset}, #{size}

SELECT count(1)
FROM t_asset_warehouse_record tawr

    tawr.warehouse_id = #{warehouseId} and tawr.del_flag = 0

        and tawr.type = #{type}


        and record_no in

            #{recordNo}
