mapper.select:
select IFNULL(sum(bean_amount),0)
from t_cloud_bean_use_record
where uid = #{uid} and type=#{type}

select
  id, enterprise_id, uid, type, bean_amount, remaining_bean_amount, package_id, franchisee_id, ref, tenant_id, create_time, update_time
from t_cloud_bean_use_record


        and create_time >= #{startTime}


        and create_time <= #{startTime}


        and uid = #{uid}


        and tenant_id = #{tenantId}


order by id desc
limit #{offset}, #{size}
