mapper.select:
select IFNULL(sum(bean_amount),0)
from t_cloud_bean_use_record
where enterprise_id = #{enterpriseId} and type=#{type}

select
id, enterprise_id, uid, type, order_type, bean_amount, remaining_bean_amount, package_id, franchisee_id, ref, tenant_id, create_time, update_time
from t_cloud_bean_use_record


        and create_time >= #{startTime}


        and create_time <= #{endTime}


        and uid = #{uid}


        and enterprise_id = #{enterpriseId}


        and type = #{type}


        and tenant_id = #{tenantId}


order by id desc
limit #{offset}, #{size}

sql.where:
e.del_flag = 0

    and r.type = #{type}



    and r.enterprise_id = #{enterpriseId}



    and r.franchisee_id = #{franchiseeId}



    AND r.tenant_id = #{tenantId}



    and r.create_time >= #{startTime}



    and r.create_time <= #{endTime}



    and r.franchisee_id in

        #{id}




    AND (
         (r.type IN ( 2, 3, 4, 5 )
    AND EXISTS ( SELECT 1 FROM t_enterprise_cloud_bean_order o WHERE o.operate_uid = #{operateUid} AND o.order_id = r.ref ))
    OR ( r.type IN ( 0, 1 )  and e.uid = #{operateUid} ))

mapper.select:
SELECT r.enterprise_id, r.franchisee_id, r.type, r.bean_amount, r.create_time, r.ref, r.id FROM t_cloud_bean_use_record r left join t_enterprise_info e on e.id = r.enterprise_id
ORDER BY r.create_time DESC
limit #{offset}, #{size}
