mapper.select:
select temco.id, temco.pay_amount as payAmount, temco.status, temco.member_card_id as packageId, temco.card_name as packageName, temco.order_id as orderNo, temco.uid, tui.name, tui.phone, temco.use_status, temco.create_time, temco.update_time from t_electricity_member_card_order temco left join t_user_info tui on tui.uid = temco.uid
temco.order_type = 1
and temco.status = 1
and temco.uid = #{uid}
and temco.enterprise_id = #{enterpriseId}
and temco.create_time >= #{beginTime}
and temco.create_time <= #{endTime}
order by temco.create_time desc

select trbo.id, trbo.order_id as orderNo, trbo.uid, tui.name, tui.phone, trbo.electricity_battery_sn, trbo.battery_deposit, trbo.electricity_cabinet_id, trbo.cell_no, trbo.type, trbo.status, trbo.order_type, trbo.create_time, trbo.update_time from t_rent_battery_order trbo left join t_user_info tui on tui.uid = trbo.uid
trbo.order_type = 1
and trbo.uid = #{uid}
and trbo.create_time >= #{beginTime}
and trbo.create_time <= #{endTime}
order by trbo.create_time desc

select dmcr.id, dmcr.member_card_name as packageName, dmcr.status, dmcr.uid, dmcr.user_name, dmcr.phone, dmcr.create_time, dmcr.update_time, dmcr.tenant_id, dmcr.err_msg, dmcr.battery_member_card_id as packageId, dmcr.disable_member_card_no as packageFreezeOrderNo, dmcr.card_days, dmcr.choose_days , dmcr.real_days , dmcr.pay_status, dmcr.charge_rate, dmcr.disable_card_time_type , dmcr.apply_reason, dmcr.disable_member_card_time as freezePackageTime, emcr.enable_time as enablePackageTime from t_ele_disable_member_card_record dmcr left join t_enable_member_card_record emcr on dmcr.disable_member_card_no=emcr.disable_member_card_no left join t_battery_member_card tbmc on dmcr.battery_member_card_id = tbmc.id
dmcr.status = 1 and tbmc.business_type = 2
and dmcr.uid = #{uid}
and dmcr.create_time >= #{beginTime}
and dmcr.create_time <= #{endTime}
order by dmcr.create_time desc

select emcr.id, emcr.member_card_name as packageName, emcr.uid, emcr.user_name, emcr.phone, emcr.enable_time as enablePackageTime, emcr.create_time, emcr.update_time, dmcr.battery_member_card_id as packageId, dmcr.disable_member_card_no as packageFreezeOrderNo, dmcr.disable_member_card_time as freezePackageTime, tbmc.business_type from t_enable_member_card_record emcr, t_ele_disable_member_card_record dmcr, t_battery_member_card tbmc where dmcr.disable_member_card_no = emcr.disable_member_card_no and dmcr.battery_member_card_id = tbmc.id and tbmc.business_type = 2
and emcr.uid = #{uid}
and emcr.create_time >= #{beginTime}
and emcr.create_time <= #{endTime}
order by emcr.create_time desc

select tero.id as id, tero.refund_order_no as depositOrderNo, tero.order_id as orderNo, tero.pay_amount as payAmount, tero.refund_amount as refundAmount, tero.status as status, tero.err_msg as errMsg, tero.create_time as createTime, tero.update_time as updateTime , tero.member_card_owe_number as memberCardOweNumber, tedo.uid as uid, tedo.name as name, tedo.phone as phone, tero.refund_order_type as refundOrderType, tedo.pay_type as payType, tedo.mid as packageId from t_ele_refund_order as tero left join t_ele_deposit_order as tedo on tero.order_id= tedo.order_id
tero.status = 4 and tedo.order_type = 1
and tedo.uid = #{uid}
and tero.create_time >= #{beginTime}
and tero.create_time <= #{endTime}
order by tero.create_time desc

select tecu.uid, tecu.renewal_status, tui.name, tui.phone, tecu.enterprise_id as enterpriseId, tubmc.member_card_id as packageId, tubmc.order_id as orderNo, tubmc.create_time as createTime, tubmc.member_card_expire_time as packageExpiredTime, tubmc.order_effective_time as effectiveTime from t_enterprise_channel_user tecu left join t_user_battery_member_card tubmc on tubmc.uid = tecu.uid left join t_user_info tui on tecu.uid = tui.uid where tecu.payment_status = 2
and tecu.tenant_id = #{tenantId}
and tecu.enterprise_id = #{enterpriseId}
and tui.name like concat('%',#{userName},'%')
and tui.phone like concat('%',#{phone},'%')
order by tubmc.member_card_expire_time desc limit #{offset}, #{size}

select tecu.uid, tecu.renewal_status, tui.name, tui.phone, tecu.enterprise_id as enterpriseId, tubmc.member_card_id as packageId, tubmc.order_id as orderNo, tubmc.create_time as createTime, tubmc.member_card_expire_time as packageExpiredTime, tubmc.order_effective_time as effectiveTime from t_enterprise_channel_user tecu left join t_user_battery_member_card tubmc on tubmc.uid = tecu.uid left join t_battery_member_card tbmc on tbmc.id = tubmc.member_card_id left join t_user_info tui on tecu.uid = tui.uid where tecu.payment_status = 2 and tbmc.business_type = 2
and tecu.enterprise_id = #{enterpriseId}
and tubmc.member_card_expire_time > #{currentTime}
and tui.name like concat('%',#{userName},'%')
and tui.phone like concat('%',#{phone},'%')
order by tubmc.member_card_expire_time asc limit #{offset}, #{size}

select tecu.uid, tecu.renewal_status, tui.name, tui.phone, tecu.create_time as createTime, tecu.update_time as updateTime from t_enterprise_channel_user tecu left join t_user_info tui on tui.uid = tecu.uid
tecu.uid is not null and tecu.payment_status=3
and tecu.enterprise_id = #{enterpriseId}
and tui.name like concat('%',#{userName},'%')
and tui.phone like concat('%',#{phone},'%')
order by tecu.id desc limit #{offset}, #{size}

select
    count(1)
from t_enterprise_channel_user tecu
left join t_user_battery_member_card tubmc on tubmc.uid = tecu.uid
left join t_user_info tui on tecu.uid = tui.uid
where
tecu.payment_status = 2

    and tecu.tenant_id = #{tenantId}



    and tecu.enterprise_id = #{enterpriseId}



    and tubmc.member_card_expire_time <= #{currentTime}



    and tui.name like concat('%',#{userName},'%')


    and tui.phone = #{phone}

select count(1) from t_enterprise_channel_user tecu left join t_user_battery_member_card tubmc on tubmc.uid = tecu.uid left join t_battery_member_card tbmc on tbmc.id = tubmc.member_card_id left join t_user_info tui on tecu.uid = tui.uid where tecu.payment_status = 2 and tbmc.business_type = 2
and tecu.enterprise_id = #{enterpriseId}
and tubmc.member_card_expire_time > #{currentTime}
and tui.name like concat('%',#{userName},'%')
and tui.phone = #{phone}

select count(1) from t_enterprise_channel_user tecu left join t_user_info tui on tui.uid = tecu.uid
tecu.uid is not null and tecu.payment_status=3
and tecu.uid not in (select uid from t_electricity_member_card_order where status = 1 and enterprise_id = #{enterpriseId})
and tecu.enterprise_id = #{enterpriseId}
and tui.name like concat('%',#{userName},'%')
and tui.phone = #{phone}
