mapper.select:
select
  id, uid, invitation_way, enterprise_id, franchisee_id, tenant_id, renewal_status, inviter_id, create_time, update_time, remark
from t_enterprise_channel_user


        and id = #{id}


        and uid = #{uid}


        and invitation_way = #{invitationWay}


        and enterprise_id = #{enterpriseId}


        and franchisee_id = #{franchiseeId}


        and tenant_id = #{tenantId}


        and renewal_status = #{renewalStatus}


        and inviter_id = #{inviterId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remark = #{remark}

mapper.insert:
insert into t_enterprise_channel_user
uid, invitation_way, enterprise_id, franchisee_id, tenant_id, renewal_status, create_time, update_time, remark
#{uid}, #{invitationWay}, #{enterpriseId}, #{franchiseeId}, #{tenantId}, #{renewalStatus}, #{createTime}, #{updateTime}, #{remark}
