mapper.select:
select

from t_mei_tuan_rider_mall_order
where mei_tuan_order_id = #{orderId}

    and mei_tuan_account = #{phone}


    and uid = #{uid}

limit 1

select

from t_mei_tuan_rider_mall_order
where tenant_id = #{tenantId} and uid = #{uid} and mei_tuan_account = #{phone} and mei_tuan_order_status != 20

    and order_id = #{orderId}

order by order_use_status desc, id desc
limit #{offset}, #{size}
