mapper.select:
select
    channel_employees_id , tenant_id , day_first_money , day_renew_money , day_total_money ,
    fee_date ,del_flag ,create_time ,update_time
from t_channel_employee_promotion_day_record

    del_flag = 0
    and fee_date  >=    #{startTime}
    and fee_date  <=    #{endTime}

limit #{offset},#{size}
