mapper.insert:
insert into t_merchant_area

    name, del_flag, create_time, update_time, tenant_id, remark


    #{name}, #{delFlag}, #{createTime}, #{updateTime}, #{tenantId}, #{remark}

mapper.update:
update t_merchant_area


        name = #{name},


        del_flag = #{delFlag},


        update_time = #{updateTime},


        remark = #{remark}



    id = #{id}

mapper.select:
select

from t_merchant_area

    del_flag = 0 and tenant_id = #{tenantId}

        and name like concat('%',#{name,jdbcType=VARCHAR},'%')


order by id desc

    limit #{offset}, #{size}

select count(1) from t_merchant_area
del_flag = 0 and tenant_id = #{tenantId}
and name like concat('%',#{name,jdbcType=VARCHAR},'%')
