mapper.insert:
insert into t_merchant_area

    name, del_flag, create_time, update_time, tenant_id, remark, franchisee_id


    #{name}, #{delFlag}, #{createTime}, #{updateTime}, #{tenantId}, #{remark}, #{franchiseeId}

mapper.update:
update t_merchant_area


        name = #{name},


        del_flag = #{delFlag},


        update_time = #{updateTime},


        remark = #{remark}



    id = #{id} and tenant_id = #{tenantId}

mapper.select:
select

from t_merchant_area

    del_flag = 0 and tenant_id = #{tenantId}

        and name like concat('%',#{name,jdbcType=VARCHAR},'%')



        and franchisee_id = #{franchiseeId}



        and franchisee_id in

            #{franchiseeId}



order by id desc

    limit #{offset}, #{size}

select count(1)
from t_merchant_area

    del_flag = 0 and tenant_id = #{tenantId}

        and name like concat('%',#{name,jdbcType=VARCHAR},'%')



        and franchisee_id = #{franchiseeId}



        and franchisee_id in

            #{franchiseeId}

select

from t_merchant_area

    del_flag = 0

        and id in

            #{id}



        and tenant_id = #{tenantId}



        and name like concat('%', concat(#{name}, '%'))



        and franchisee_id = #{franchiseeId}



        and franchisee_id in

            #{franchiseeId}



order by id desc

    limit #{offset}, #{size}

select

from t_merchant_area
where del_flag = 0 and id= #{id}

select
from t_merchant_area where del_flag = 0 and id in
#{id}
