mapper.select:
select any_value(id) as id,
       any_value(date) as date,
       sum(place_count) as placeCount,
       sum(month_sum_power) as monthSumPower,
       sum(month_sum_charge) as monthSumCharge,
       any_value(tenant_id) as tenantId
from t_merchant_cabinet_power_month_record

    del_flag = 0

        and date = #{monthDate}


        and tenant_id = #{tenantId}


        and franchisee_id in

            #{franchiseeId}



        and franchisee_id = #{franchiseeId}


order by date desc

    limit #{offset}, #{size}

select count(1)
from (select any_value(id) as id,
any_value(date) as date,
sum(place_count) as placeCount,
sum(month_sum_power) as monthSumPower,
sum(month_sum_charge) as monthSumCharge,
any_value(tenant_id) as tenantId
from t_merchant_cabinet_power_month_record

    del_flag = 0

        and date = #{monthDate}


        and tenant_id = #{tenantId}


        and franchisee_id in

            #{franchiseeId}



        and franchisee_id = #{franchiseeId}


) a
